INSERT INTO `#__shop_help` (`h_id`, `h_title`, `h_content`, `h_parent_id`, `h_level_id`, `h_seq_num`) VALUES(1, '首页问题汇总', '<div class="main-content">\r\n				<div class="inner">\r\n					<ul class="path">\r\n						<li>帮助中心首页</li>\r\n					</ul>\r\n				</div>\r\n				<!-- end of inner -->\r\n				<div class="text">\r\n					<h4>首页问题汇总</h4>\r\n					<p>1. <a href="#" onclick="$(''#main'').load(''seller_help/published/index.html'');">如何发布宝贝。</a></p>\r\n					<p>会员登录后，点击我要卖就可以发布宝贝了；</p>\r\n					<p>2. <a href="#" onclick="$(''#main'').load(''seller_help/open_shop/index.html'');">如何开店。</a></p>\r\n					<p>会员注册后，进入我的商城，点击免费开店；</p>\r\n					<p>3. <a href="#" onclick="$(''#main'').load(''bought_help/buy/index.html'');">如何购买。</a></p>\r\n					<p>打开想要购买的商品页面，点击立刻购买；</p>\r\n				</div>\r\n				<!-- end of text -->\r\n			</div>\r\n			<!-- end of main-content -->', 0, 1, 1);
